import loggingimport logging.configlogging.config.fileConfig(fname='../utils/logging_to_log.conf')logger = logging.getLogger('data_store')def extract_file(df,num_partition,format,file_path,headerreq,compressiontype):    try:        logger.info("extract_file() function is started.......")        (df.coalesce(num_partition).            write.            format(format).            option('header',headerreq).            option('compression',compressiontype).            save(file_path))    except Exception as exp:        logger.error("Error in the method - extract_file(). ",str(exp))        raise    else:        logger.info('Extraction - extract_file is Completed.....')